export function flow<A extends Array<unknown>, B, C, D>(
  ab: (...a: A) => B,
  bc: (b: B) => C,
  cd: (b: C) => D,
): (...a: A) => D;
export function flow(...fns: Array<Function>): Function {
  const len = fns.length - 1;
  return function(this: any, ...x: Array<any>) {
    let y = fns[0].apply(this, x);
    for (let i = 1; i <= len; i++) {
      y = fns[i].call(this, y);
    }
    return y;
  };
}
