import React from "react";

function AboutInfo(props) {
  return (
    <div className="about">
      <div className="about__txt">
        <h3>{props.title}</h3>
        <p>{props.desc}</p>
        <div className="about__img"></div>
      </div>
    </div>
  );
}

const aboutText = [
  {
    title: "유튜브 API",
    desc: "유튜브에서 제공하는 API를 가져와서 작업했습니다.",
    img: "img/aboutImg01.jpg",
  },
  {
    title: "TMDB API",
    desc: "TMDB에서 제공하는 API를 가져와서 작업했습니다.",
    img: "img/aboutImg02.jpg",
  },
  {
    title: "Unsplash API",
    desc: "Unsplash에서 제공하는 API를 가져와서 작업했습니다.",
    img: "img/aboutImg03.jpg",
  },
];

function AboutCont() {
  return (
    <section className="about__cont">
      <div className="container">
        <div className="about__inner">
          {aboutText.map((txt) => (
            <AboutInfo key={txt.title} title={txt.title} desc={txt.desc} />
          ))}
        </div>
      </div>
    </section>
  );
}

export default AboutCont;
