import React from "react";
import Header from "../layout/Header";
import Contents from "../layout/Contents";
import Footer from "../layout/Footer";
import Title from "../layout/Title";
import Contact from "../layout/Contact";
import MovieCont from "../includes/MovieCont";
import { useState } from "react";
import { useEffect } from "react";

const TMDB_KEY = process.env.REACT_APP_TMDB_KEY;
const BASE_URL = "https://api.themoviedb.org/3";
const BASE_LANG = "ko";
const BASE_REGION = "KR";

export const getPopularMovies = () => {
  return `${BASE_URL}/movie/popular?api_key=${TMDB_KEY}&language=${BASE_LANG}&region=${BASE_REGION}`;
};

// Images SAMPLE
// https://image.tmdb.org/t/p/w500/kqjL17yufvn9OVLyXYpvtyrFfak.jpg
export const getImageUrl = (path, size = 400) => {
  return `https://image.tmdb.org/t/p/w${size}${path}`;
};

export const STATUS = {
  pending: "pending",
  resolved: "resolved",
  error: "error",
};

const { pending, resolved, error } = STATUS;

const useFetchData = (api) => {
  const [status, setStatus] = useState(pending);
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setStatus(pending);
      try {
        const response = await fetch(api);
        const jsonData = await response.json();
        setData(jsonData);
        setStatus(resolved);
      } catch (e) {
        setError(e);
        setStatus(error);
      }
    };

    fetchData(api);
  }, []);

  return [status, data, error];
};
